mapper.select:
SELECT report_date as reportDate, betting_money as bettingMoney,winning_money as winningMoney , CASE when platform_money >= 0 then platform_money ELSE 0 END as platformMoney ,
CASE when platform_money <= 0 then platform_money ELSE 0 END as platformLossMoney
from system_finance_account_report WHERE 1=1 ${queryCond}
